import { useEffect, useState } from "react";
import { api } from "../service/Api";
import ProductForm from "../components/ProductForm";
import "../ProductPage.css";
import "../initialData.json";




function ProductPage() {
  const [products, setProducts] = useState([]);
  const [editingProduct, setEditingProduct] = useState(null);
  const [productMaterials, setProductMaterials] = useState([]);
  const [selectedRawMaterial, setSelectedRawMaterial] = useState("");
  const [requiredQuantity, setRequiredQuantity] = useState("");
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [rawMaterials, setRawMaterials] = useState([]);

  const loadProductMaterials = (productId) => {
  api.get(`/product-raw-materials/${productId}/raw-materials`)
    .then(res => setProductMaterials(res.data))
    .catch(err => console.error(err));
};


  const handleAddRawMaterial = () => {
  if (!selectedProduct || !selectedRawMaterial || !requiredQuantity) {
    alert("Fill all fields");
    return;
  }

  api.post(`/product-raw-materials/${selectedProduct.id}/add`, {
    rawMaterialId: selectedRawMaterial,
    requiredQuantity: requiredQuantity
  })
  .then(() => {
    setSelectedRawMaterial("");
    setRequiredQuantity("");
    loadProductMaterials(selectedProduct.id);
  })
  .catch(err => console.error(err));
};


const handleRemoveRawMaterial = (associationId) => {
  api.delete(`/product-raw-materials/${associationId}`)
    .then(() => {
      loadProductMaterials(selectedProduct.id);
    })
    .catch(err => console.error("Error removing raw material:", err));
};


  const loadProducts = () => {
    api.get("/products")
      .then(response => {
        setProducts(response.data.content || []);
      })
      .catch(err => console.error(err));
  };
  useEffect(() => {
  api.get("/products")
    .then(res => {
      const products = res.data.content || [];
      if (products.length === 0) {
        setProducts(initialData.products); // pré-carregar
      } else {
        setProducts(products);
      }
    })
    .catch(() => {
      setProducts(initialData.products);
    });

  api.get("/raw-materials")
    .then(res => {
      const materials = res.data.content || res.data;
      if (materials.length === 0) {
        setRawMaterials(initialData.rawMaterials); // pré-carregar
      } else {
        setRawMaterials(materials);
      }
    })
    .catch(() => {
      setRawMaterials(initialData.rawMaterials);
    });
}, []);

  useEffect(() => {
    loadProducts();
  }, []);

  useEffect(() => {
  loadProducts();

  api.get("/raw-materials")
    .then(response => {
      setRawMaterials(response.data.content || response.data);
    })
    .catch(error => {
      console.error("Error fetching raw materials:", error);
    });
}, []);


  const handleDelete = async (id) => {
    if (!window.confirm("Delete this product?")) return;

    try {
      await api.delete(`/products/${id}`);
      loadProducts();
    } catch (error) {
      console.error("Error deleting product", error);
    }
  };

  return (
    <div>
      <h1>Products</h1>

      <ProductForm
        product={editingProduct}
        onSuccess={() => {
          setEditingProduct(null);
          loadProducts();
        }}
        onCancel={() => setEditingProduct(null)}
      />
{selectedProduct && (
  <div>
    <h3>Raw Materials</h3>

    <select
      value={selectedRawMaterial}
      onChange={e => setSelectedRawMaterial(e.target.value)}
    >
      <option value="">Select raw material</option>
      {rawMaterials.map(rm => (
        <option key={rm.id} value={rm.id}>
          {rm.name}
        </option>
      ))}
    </select>

    <input
      type="number"
      placeholder="Required quantity"
      value={requiredQuantity}
      onChange={e => setRequiredQuantity(e.target.value)}
    />

    <button onClick={handleAddRawMaterial}>
      Add
    </button>

    <ul>
  {productMaterials.map(pm => (
    <li key={pm.id}>
      {pm.rawMaterialName} – {pm.requiredQuantity}
      <button onClick={() => handleRemoveRawMaterial(pm.id)}>Remove</button>
    </li>
  ))}
</ul>


  </div>
)}

      <table border="1">
        <thead>
          <tr>
            <th>Code</th>
            <th>Name</th>
            <th>Price</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {products.map(p => (
            <tr key={p.id}>
              <td>{p.code}</td>
              <td>{p.name}</td>
              <td>{p.price}</td>
              <td>
                <button onClick={() => setEditingProduct(p)}>Edit</button>
                <button onClick={() => handleDelete(p.id)}>Delete</button>
                <button onClick={() => setSelectedProduct(p)}>Raw Materials</button>

              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ProductPage;
