import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { fetchProducts } from "../redux/produceSlice";

const ProductPage = () => {
  const dispatch = useDispatch();

  // Pega o array de produtos do Redux
  const products = useSelector((state) => state.products.items);
  const status = useSelector((state) => state.products.status);
  const error = useSelector((state) => state.products.error);
  const [selectedProduct, setSelectedProduct] = React.useState(null);
  const [isModalOpen, setIsModalOpen] = React.useState(false);

  

  useEffect(() => {
    dispatch(fetchProducts());
  }, [dispatch]);

  const handleViewDetails = (product) => {
    setSelectedProduct(product);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setSelectedProduct(null);
    setIsModalOpen(false);
  }

  const handleAdjustStock = (product) => {
  product.rawMaterials.forEach((rm) => {
    const quantity = parseInt(prompt(`Adjust stock for ${rm.rawMaterialName} (current: ${rm.quantity})`));
    if (!isNaN(quantity)) {
      dispatch(adjustStock({ id: rm.rawMaterialId, quantity }));
    }
  });
};


  if (status === "loading") {
    return <p>Loading products...</p>;
  }

  if (status === "failed") {
    return <p>Error fetching products: {error}</p>;
  }

  return (
    <div>
      <h1>Products</h1>

      {status === "succeeded" && Array.isArray(products) && products.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>Code</th>
              <th>Name</th>
              <th>Price</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {products.map((product) => (
              <tr key={product.code}>
                <td>{product.code}</td>
                <td>{product.name}</td>
                <td>{product.price}</td>
                <td>
                  <button onClick={() => handleViewDetails(product)}>View Details</button>
                  <button onClick={() => handleAdjustStock(product)}>Adjust Stock</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No products found</p>
      )}
      {isModalOpen && selectedProduct && (
  <div className="modal">
    <h2>{selectedProduct.name} Details</h2>
    <p>Code: {selectedProduct.code}</p>
    <p>Price: {selectedProduct.price}</p>
    <h3>Raw Materials:</h3>
    <ul>
      {selectedProduct.rawMaterials && selectedProduct.rawMaterials.map((rm) => (
        <li key={rm.rawMaterialId}>
          {rm.rawMaterialName} - Required: {rm.requiredQuantity} - Stock: {rm.quantity || 'N/A'}
        </li>
      ))}
    </ul>
    <button onClick={handleCloseModal}>Close</button>
  </div>
)}      
    </div>
  );
};

export default ProductPage;
