import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";

const API_URL = "http://localhost:8080"; 

// Async thunk para ajustar estoque
export const adjustStock = createAsyncThunk(
  "rawMaterials/adjustStock",
  async ({ id, quantity }, { rejectWithValue }) => {
    try {
      const response = await axios.patch(`${API_URL}/raw-materials/${id}/adjust`, null, {
        params: { quantity },
      });
      return response.data; // retorna o raw material atualizado
    } catch (err) {
      return rejectWithValue(err.response.data);
    }
  }
);

const rawMaterialSlice = createSlice({
  name: "rawMaterials",
  initialState: {
    items: [],
    status: "idle",
    error: null,
  },
  reducers: {
    setRawMaterials: (state, action) => {
      state.items = action.payload;
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(adjustStock.fulfilled, (state, action) => {
        // atualiza a lista local
        const index = state.items.findIndex(rm => rm.id === action.payload.id);
        if (index !== -1) {
          state.items[index] = action.payload;
        }
      });
  },
});

export const { setRawMaterials } = rawMaterialSlice.actions;
export default rawMaterialSlice.reducer;
