import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { fetchProducts } from "../redux/ProduceSlice";
import { fetchRawMaterials } from "../redux/RawMaterialSlice";

// Função para chamar o endpoint de associação
const associateRawMaterialToProduct = async (productId, rawMaterialId, requiredQuantity) => {
  const response = await fetch(
    `http://localhost:8080/product-raw-materials/${productId}/add?rawMaterialId=${rawMaterialId}&requiredQuantity=${requiredQuantity}`,
    { method: "POST" }
  );

  if (!response.ok) throw new Error("Failed to associate raw material");
  return await response.json();
};

const rawMaterials = useSelector(state => state.rawMaterials.items || []);


const ProductRawMaterialPage = () => {
  const dispatch = useDispatch();
  const products = useSelector((state) => state.products.items);
  const rawMaterials = useSelector((state) => state.rawMaterials.items);

  const [selectedProduct, setSelectedProduct] = useState(null);
  const [selectedRawMaterial, setSelectedRawMaterial] = useState(null);
  const [quantity, setQuantity] = useState("");

  useEffect(() => {
    dispatch(fetchProducts());
    dispatch(fetchRawMaterials());
  }, [dispatch]);

  const handleAssociate = async () => {
    if (!selectedProduct || !selectedRawMaterial || !quantity) {
      alert("Please select product, raw material and quantity");
      return;
    }

    try {
      await associateRawMaterialToProduct(selectedProduct.productCode, selectedRawMaterial.rawMaterialId, parseInt(quantity));
      alert("Association successful!");
      setQuantity("");
    } catch (error) {
      alert(error.message);
    }
  };

  return (
    <div>
      <h1>Associate Raw Material to Product</h1>

      <div>
        <label>Product: </label>
        <select onChange={(e) => setSelectedProduct(JSON.parse(e.target.value))}>
          <option value="">Select product</option>
          {products.map((p) => (
            <option key={p.productCode} value={JSON.stringify(p)}>
              {p.productName}
            </option>
          ))}
        </select>
      </div>

      <div>
        <label>Raw Material: </label>
        <select onChange={(e) => setSelectedRawMaterial(JSON.parse(e.target.value))}>
          <option value="">Select raw material</option>
          {rawMaterials.map((rm) => (
            <option key={rm.rawMaterialId} value={JSON.stringify(rm)}>
              {rm.rawMaterialName}
            </option>
          ))}
        </select>
      </div>

      <div>
        <label>Required Quantity: </label>
        <input
          type="number"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
        />
      </div>

      <button onClick={handleAssociate}>Associate</button>
    </div>
  );
};

export default ProductRawMaterialPage;
