import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";

export const fetchRawMaterials = createAsyncThunk(
  "rawMaterials/fetchRawMaterials",
  async () => {
    const response = await axios.get("http://localhost:8080/raw-materials");
    return response.data;
  }
);

const rawMaterialSlice = createSlice({
  name: "rawMaterials",
  initialState: { items: [], status: "idle" },
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(fetchRawMaterials.pending, (state) => { state.status = "loading"; })
      .addCase(fetchRawMaterials.fulfilled, (state, action) => { 
        state.status = "succeeded"; 
        state.items = action.payload; 
      })
      .addCase(fetchRawMaterials.rejected, (state) => { state.status = "failed"; });
  },
});

export default rawMaterialSlice.reducer;
