import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { fetchRawMaterials, adjustStock } from "../redux/RawMaterialSlice";

const RawMaterialPage = () => {
  const dispatch = useDispatch();
  const rawMaterials = useSelector((state) => state.rawMaterials.items);
  const status = useSelector((state) => state.rawMaterials.status);
  const error = useSelector((state) => state.rawMaterials.error);

  useEffect(() => {
    dispatch(fetchRawMaterials());
  }, [dispatch]);

  const handleAdjustStock = (rm) => {
    const quantity = parseInt(prompt(`Adjust stock for ${rm.rawMaterialName} (current: ${rm.currentStock})`));
    if (!isNaN(quantity)) {
      dispatch(adjustStock({ id: rm.rawMaterialId, quantity }));
    }
  };

  if (status === "loading") return <p>Loading raw materials...</p>;
  if (status === "failed") return <p>Error: {error}</p>;

  return (
    <div>
      <h1>Raw Materials</h1>
      {rawMaterials.length === 0 ? (
        <p>No raw materials found</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Code</th>
              <th>Name</th>
              <th>Stock</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {rawMaterials.map((rm) => (
              <tr key={rm.rawMaterialId}>
                <td>{rm.rawMaterialCode}</td>
                <td>{rm.rawMaterialName}</td>
                <td>{rm.currentStock}</td>
                <td>
                  <button onClick={() => handleAdjustStock(rm)}>Adjust Stock</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default RawMaterialPage;
