import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { fetchProducts } from "../redux/produceSlice";

const ProductPage = () => {
  const dispatch = useDispatch();

  // Pega o array de produtos do Redux
  const products = useSelector((state) => state.products.items);
  const status = useSelector((state) => state.products.status);
  const error = useSelector((state) => state.products.error);

  useEffect(() => {
    dispatch(fetchProducts());
  }, [dispatch]);

  if (status === "loading") {
    return <p>Loading products...</p>;
  }
  if (status === "succeeded" && products.length === 0) {
    return <p>No products available.</p>;
  }

  if (status === "failed") {
    return <p>Error fetching products: {error}</p>;
  }

return (
  <div>
    <h1>Products</h1>
    {products.length === 0 ? (
      <p>No products found</p>
    ) : (
      <table>
        <thead>
          <tr>
            <th>Code</th>
            <th>Name</th>
            <th>Price</th>
            <th>Actions</th> {/* nova coluna */}
          </tr>
        </thead>
        <tbody>
          {products.map((product) => (
            <tr key={product.code}>
              <td>{product.code}</td>
              <td>{product.name}</td>
              <td>{product.price}</td>
              <td>
                <button onClick={() => handleViewDetails(product)}>View Details</button>
                <button onClick={() => handleAdjustStock(product)}>Adjust Stock</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    )}
  </div>
);
};

export default ProductPage;
