import { useEffect, useState } from "react";
import { api } from "../service/Api";
import ProductForm from "../components/ProductForm";
import "../ProductPage.css";
import initialData from "../initialData.json";

function ProductPage() {
  const [products, setProducts] = useState([]);
  const [editingProduct, setEditingProduct] = useState(null);
  const [productMaterials, setProductMaterials] = useState([]);
  const [selectedRawMaterial, setSelectedRawMaterial] = useState("");
  const [requiredQuantity, setRequiredQuantity] = useState("");
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [rawMaterials, setRawMaterials] = useState([]);

  // Carregar matérias-primas de um produto selecionado
  const loadProductMaterials = (productId) => {
    api.get(`/product-raw-materials/${productId}/raw-materials`)
      .then(res => setProductMaterials(res.data))
      .catch(err => console.error(err));
  };

  // Adicionar matéria-prima a um produto
  const handleAddRawMaterial = () => {
    if (!selectedProduct || !selectedRawMaterial || !requiredQuantity) {
      alert("Fill all fields");
      return;
    }

    api.post(`/product-raw-materials/${selectedProduct.id}/add`, {
      rawMaterialId: selectedRawMaterial,
      requiredQuantity: requiredQuantity
    })
      .then(() => {
        setSelectedRawMaterial("");
        setRequiredQuantity("");
        loadProductMaterials(selectedProduct.id);
      })
      .catch(err => console.error(err));
  };

  // Remover matéria-prima de um produto
  const handleRemoveRawMaterial = (associationId) => {
    api.delete(`/product-raw-materials/${associationId}`)
      .then(() => {
        loadProductMaterials(selectedProduct.id);
      })
      .catch(err => console.error("Error removing raw material:", err));
  };

  // Carregar produtos do backend ou JSON
  const loadProducts = () => {
    api.get("/products")
      .then(response => {
        const productsData = response.data.content || [];
        setProducts(productsData.length > 0 ? productsData : initialData.products);
      })
      .catch(() => {
        setProducts(initialData.products);
      });
  };

  // Carregar matérias-primas do backend ou JSON
  const loadRawMaterials = () => {
    api.get("/raw-materials")
      .then(response => {
        const materialsData = response.data.content || response.data;
        setRawMaterials(materialsData.length > 0 ? materialsData : initialData.rawMaterials);
      })
      .catch(() => {
        setRawMaterials(initialData.rawMaterials);
      });
  };

  useEffect(() => {
    loadProducts();
    loadRawMaterials();
  }, []);

  const handleDelete = async (id) => {
    if (!window.confirm("Delete this product?")) return;

    try {
      await api.delete(`/products/${id}`);
      loadProducts();
    } catch (error) {
      console.error("Error deleting product", error);
    }
  };

  return (
    <div className="product-page">
      <h1>Products</h1>

      <ProductForm
        product={editingProduct}
        onSuccess={() => {
          setEditingProduct(null);
          loadProducts();
        }}
        onCancel={() => setEditingProduct(null)}
      />

      {selectedProduct && (
        <div className="raw-materials-section">
          <h3>Raw Materials for {selectedProduct.name}</h3>

          <div className="raw-materials-form">
            <select
              value={selectedRawMaterial}
              onChange={e => setSelectedRawMaterial(e.target.value)}
            >
              <option value="">Select raw material</option>
              {rawMaterials.map(rm => (
                <option key={rm.id} value={rm.id}>
                  {rm.name}
                </option>
              ))}
            </select>

            <input
              type="number"
              placeholder="Required quantity"
              value={requiredQuantity}
              onChange={e => setRequiredQuantity(e.target.value)}
            />

            <button onClick={handleAddRawMaterial}>Add</button>
          </div>

          <ul className="raw-materials-list">
            {productMaterials.map(pm => (
              <li key={pm.id}>
                {pm.rawMaterialName} – {pm.requiredQuantity}
                <button onClick={() => handleRemoveRawMaterial(pm.id)}>Remove</button>
              </li>
            ))}
          </ul>
        </div>
      )}

      <table className="products-table">
        <thead>
          <tr>
            <th>Code</th>
            <th>Name</th>
            <th>Price</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {products.map(p => (
            <tr key={p.id}>
              <td>{p.code}</td>
              <td>{p.name}</td>
              <td>{p.price}</td>
              <td>
                <button onClick={() => setEditingProduct(p)}>Edit</button>
                <button onClick={() => handleDelete(p.id)}>Delete</button>
                <button onClick={() => {
                  setSelectedProduct(p);
                  loadProductMaterials(p.id);
                }}>Raw Materials</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ProductPage;
